import axios from "axios";
import React, { useState } from "react";

function Form({ setRefresh, heading }) {
  const [data, setData] = useState();
  const [colorState, setColorState] = useState(true);
  function onSubmit(e) {
    e.preventDefault();
    axios.post("http://localhost:8000/add-user", data).then((response) => {
      setRefresh();
      alert("user added");
    });
  }
  function colorClick(e) {
    e.preventDefault();
    setColorState(colorState === false ? true : false);
  }
  function onChange(e) {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  }

  return (
    <form className="main-form p-5 mt-5 mx-auto">
      <div
        className="rounded-pill mx-auto"
        style={{ background: "blue", width: "fit-content" }}
      >
        <button
          style={{ background: colorState === true ? "" : "blue" }}
          className="rounded-pill p-2  pl-4 pr-4 button-1"
          onClick={colorClick}
        >
          Fan Signup
        </button>
        <button
          className="rounded-pill button-2 p-2  pl-4 pr-4 "
          style={{ background: colorState === true ? "blue" : "" }}
          onClick={colorClick}
        >
          Talen Signup
        </button>
      </div>

      <div className="mx-auto">
        <h3 className="text-white mt-5">Create Your Fan Accout</h3>
      </div>
      <div className="mx-auto">
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" value="Last name" />
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
          <small>lasttname</small>
          </div>
          <input class="ip p-1" value="Last name" />
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" value="Last name"/>
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" value="Last name"/>
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" value="Last name" />
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1"  value="Last name" />
        </div>
        <div className="row">
            <button type="submit" className="rounded-pill mx-auto signup-button">SIGN UP</button>
        </div>
      </div>
    </form>
  );
}

export default Form;
