import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Table({refresh}) {
    const [users, setUsers] = useState([])
    const [status, setStatus] = useState(true)
    useEffect(() => {
        axios.get('http://localhost:8000/get-users').then((response) => {
            console.log(response)
            setUsers(response.data)
            console.log(refresh)
        })
    },[])
    function edit (id) {
console.log(id)
setStatus(false)
    }
    return (
        <div>
            <table>

  <thead><tr>
    <th>Id</th>
    <th>name</th>
    <th>email</th>
  </tr>
  </thead>
  <tbody>
  {users.map((data) => {
      return(<tr key={data.id}>
      <td>{data.id}</td>
      <td><input defaultValue={data.name} name="name" readOnly={false} />{data.name}</td>
      <td><input defaultValue={data.email} name="email" readOnly={status}/>{data.email}</td>
      <td><button onClick={()=> edit(data.id)}>edit</button></td>
    </tr>)
  })}
  </tbody>


</table>
        </div>
    )
}

export default Table
