import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Row from './Row';

function Table({setRefresh, refresh, heading}) {
    const [users, setUsers] = useState([])

    useEffect(() => {
        console.log("blahj j jjj")
        axios.get('http://localhost:8000/get-users/'+heading).then((response) => {
            console.log('re rendering')
            setUsers(response.data)
            console.log(users)

        })
    },[refresh,heading])
  function changeRefresh () {
      setRefresh()
  }


    return (
        <div>
            <h6>{heading}</h6>
            <table>

  <thead><tr>
    <th>Id</th>
    <th>name</th>
    <th>email</th>
  </tr>
  </thead>
  <tbody>
  {users.map((data) => {
      return(<Row key={heading==='mongo'?data._id:data.oid} setRefresh={changeRefresh} id={heading==='mongo'?data._id:data.oid}  name={data.name} heading={heading}  email={data.email} />
      )
  })}
  </tbody>


</table>
        </div>
    )
}

export default Table
