            }
            function onChange(e) {
                setInfo({
                    ...info,
                    [e.target.name]:e.target.value
                })
            }
            function onSubmit () {

                axios.post('http://localhost:8000/edit-user/'+data.id,info).then((response) => {
                    alert("user edited")
                    data.setRefresh()
                })
            }
    return (

        <tr key={data.id}>
      <td>{0}</td>
      <td><input defaultValue={data.name} name="name" onChange={onChange} readOnly={status} /></td>
      <td><input defaultValue={data.email} name="email" onChange={onChange} readOnly={status}/></td>
      <td><button onClick={()=> edit(data.id)}>edit</button></td>
      <td><button onClick={onSubmit}>post</button></td>
    </tr>
    )
}

export default Row
