import { useEffect, useState } from 'react';
import './App.css';
import Form from './Form';
import Table from './Table';
function App() {
  const [refresh, setRefresh] = useState(false) 
  function refreshPage () {
    setRefresh(true)
  }
  useEffect(() => {
    console.log(refresh)
  },[refresh])
  return (
    <div className="App">
      <Table refresh={refresh} database="mongo"/>
      <Table refresh={refresh} database="sql" style={{float:'left'}}/>
      <Form heading="mongo" setRefresh={refreshPage}/>
      <Form heading="postgresSql" setRefresh={refreshPage}/>
    </div>
  );
}

export default App;
