        axios.get('http://localhost:8000/get-users').then((response) => {
            console.log(response)
            setUsers(response.data)
            console.log(refresh)
        })
    },[])
    function edit (id) {
console.log(id)
    }
    return (
        <div>
            <table>

  <thead><tr>
    <th><input/>Id</th>
    <th>name</th>
    <th>email</th>
  </tr>
  </thead>
  <tbody>
  {users.map((data) => {
      return(<tr key={data.id}>
      <td>{data.id}</td>
      <td>{data.name}</td>
      <td>{data.email}</td>
      <button onClick={()=> edit(data.id)}>edit</button>
    </tr>)
  })}
  </tbody>


</table>
        </div>
    )
}

export default Table
