import axios from "axios";
import React, { useState } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";


const initialValues = {
  firstname: "",
  lastname: "",
  username: "",
  email: "",
  timezone: "",
  password:""

};
const validationSchema = Yup.object({
  firstname: Yup.string().required("This field is required"),
  lastname: Yup.string().required("This field is required"),
  username: Yup.string().required("This field is required"),
  email: Yup.string().email().required("Email is required"),
  timezone:Yup.string().required("This field is required"),
  password:Yup.string()
  .required("Password is required")
  .min(4, "Password is too short - should be 4 chars minimum"),

});

function Form({ setRefresh, heading }) {
  const [data, setData] = useState();
  const [colorState, setColorState] = useState(true);
  const formik = useFormik({
    initialValues,
    onSubmit,
    validationSchema
  })

  function onSubmit(formData) {
   if(colorState){
    axios.post("http://localhost:8000/submit-fan", formData).then((response) => {
      alert("Success");
    })  .catch((err) => {
      alert("Erro")
    })
   }
   else{
    axios.post("http://localhost:8000/submit-talent", formData).then((response) => {

      alert("Succes");
    })
    .catch((err) => {
      alert("Erro")
    })

   }

  }
  function colorClick(e) {
    e.preventDefault();
    setColorState(colorState === false ? true : false);
  }
  function onChange(e) {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  }

  return (
    <form className="main-form p-5 mt-5 mx-auto" onSubmit={formik.handleSubmit}>
      <div
        className="rounded-pill mx-auto"
        style={{ background: "#121212", width: "fit-content" }}
      >
        <button
          style={{ background: colorState === true ? "#10ff8d" : "#121212" }}
          className="rounded-pill p-2  pl-4 pr-4 button-1"
          onClick={colorClick}
        >
          Fan Signup
        </button>
        <button
          className="rounded-pill button-2 p-2  pl-4 pr-4 "
          style={{ background: colorState === true ? "#121212" : "#10ff8d" }}
          onClick={colorClick}
        >
          Talen Signup
        </button>
      </div>

      <div className="row">
        <h3 className="text-white mt-5 mx-auto">Create Your {colorState===true?'Fan':'Talent'} Account</h3>
      </div>
      <div className="mx-auto">
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" placeholder="first name" name="firstname" {...formik.getFieldProps("firstname")} />
          {formik.touched.firstname && formik.errors.firstname ? (
          <div className="text-danger">{formik.errors.firstname}</div>
        ) : null}
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
          <small>lasttname</small>
          </div>
          <input class="ip p-1" placeholder="Last name" name="lastname" {...formik.getFieldProps("lastname")} />
          {formik.touched.lastname && formik.errors.lastname ? (
          <div className="text-danger">{formik.errors.lastname}</div>
        ) : null}
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>username</small>
          </div>
          <input class="ip p-1" placeholder="username" name="username" {...formik.getFieldProps("username")}/>
          {formik.touched.username && formik.errors.username ? (
          <div className="text-danger">{formik.errors.username}</div>
        ) : null}
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" placeholder="email" name="email" {...formik.getFieldProps("email")}/>
          {formik.touched.email && formik.errors.email ? (
          <div className="text-danger">{formik.errors.email}</div>
        ) : null}
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>firstname</small>
          </div>
          <input class="ip p-1" placeholder="timezone" name="time" {...formik.getFieldProps("timezone")} />
          {formik.touched.timezone && formik.errors.timezone ? (
          <div className="text-danger">{formik.errors.timezone}</div>
        ) : null}
        </div>
        <div className="m-4 ml-5 mr-5">
          <div>
            <small>password</small>
          </div>
          <input class="ip p-1" type="password" placeholder="password" name="username" {...formik.getFieldProps("password")} />
          {formik.touched.password && formik.errors.password ? (
          <div className="text-danger">{formik.errors.password}</div>
        ) : null}
        </div>
        <div className="row">
            <button type="submit" className="rounded-pill mx-auto pt-2 pl-5 pr-5 pb-2 signup-button">SIGN UP</button>
        </div>
      </div>
    </form>
  );
}

export default Form;
