import React from 'react'

function Row(data) {
    const [status, setStatus] = useState(true)
    function edit (id) {
        console.log(id)
        setStatus(false)
            }
    return (
        <tr key={data.id}>
      <td>{data.id}</td>
      <td><input defaultValue={data.name} name="name" readOnly={status} /></td>
      <td><input defaultValue={data.email} name="email" readOnly={status}/></td>
      <td><button onClick={()=> edit(data.id)}>edit</button></td>
    </tr>
    )
}

export default Row
